<script>
import { search } from '@/api/search'

export default {
  name: 'SearchPage',
  data() {
    return {
      currentResult: [],
      searchDetail: [],
      currentPage: 1,
      pageSize: 8,
      pageLength: 20,
      pageVisible: 10,
      searchText: '',
      dialog: false,
      myFiles: [],
      setting: false,
    }
  },
  mounted() {
    search({
      limit: 0,
      text: this.$route.query.q,
      stopWord: '',
    }).then((res) => {
      this.searchDetail = res.data.Result
      const cur = this.pageSize * this.currentPage
      const prev = this.pageSize * this.currentPage - this.pageSize
      this.pageLength = Math.ceil(this.searchDetail.length / this.pageSize)
      this.currentResult = this.searchDetail.slice(prev, cur)
    })
  },

  methods: {
    SearchResult() {
      search({
        limit: 0,
        text: this.searchText,
        stopWord: '',
      }).then((res) => {
        this.searchDetail = res.data.Result
        const cur = this.pageSize * this.currentPage
        const prev = this.pageSize * this.currentPage - this.pageSize
        this.pageLength = Math.ceil(this.searchDetail.length / this.pageSize)
        this.currentResult = this.searchDetail.slice(prev, cur)
        this.currentPage = 1
        this.$router.push({ name: 'images', query: { q: this.searchText } })
      })
    },
    onPageChange(page) {
      this.currentPage = page
      const cur = this.pageSize * this.currentPage
      const prev = this.pageSize * this.currentPage - this.pageSize
      this.currentResult = this.searchDetail.slice(prev, cur)
    },
    handleFilePondInit() {
      console.log('FilePond has initialized')
    },
    set() {
      this.setting = true
    },
  },
}
</script>

